import React from "react";
import { useWallet, Wallet } from "@solana/wallet-adapter-react";
import { WalletName, WalletReadyState } from "@solana/wallet-adapter-base";

import { useOs } from "~/hooks/useOs";

type WalletPreset = "eth" | "sol" | "social";

const ETH_PRESET = ["Backpack", "Phantom", "WalletConnect", "MetaMask", "Ethereum Wallet"];
const SOL_PRESET = ["Backpack", "Phantom", "Solflare"];
const SOCIAL_PRESET = ["Backpack", "Phantom", "Solflare"];

const BackpackWalletName = "Backpack" as WalletName<"Backpack">;

const PhantomWalletName = "Phantom" as WalletName<"Phantom">;

const SolflareWalletName = "Solflare" as WalletName<"Solflare">;

export interface ExtendedWallet extends Wallet {
  deeplink?: string;
  installLink?: string;
}

export function useAvailableWallets(preset?: WalletPreset): ExtendedWallet[] {
  const { wallets } = useWallet();
  const { isAndroid, isIOS } = useOs();

  const selectedPreset = React.useMemo(() => {
    if (preset === "eth") return ETH_PRESET;
    else if (preset === "sol") return SOL_PRESET;
    else if (preset === "social") return SOCIAL_PRESET;
  }, [preset]);

  const isMobile = React.useMemo(() => isAndroid || isIOS, [isAndroid, isIOS]);

  const isBackpackInstalled = React.useMemo(
    () => !!wallets.find((wallet) => wallet.adapter.name === "Backpack" && wallet.readyState === "Installed"),
    [wallets]
  );

  const isPhantomInstalled = React.useMemo(
    () => !!wallets.find((wallet) => wallet.adapter.name === "Phantom" && wallet.readyState === "Installed"),
    [wallets]
  );

  const isSolflareInstalled = React.useMemo(
    () => !!wallets.find((wallet) => wallet.adapter.name === "Solflare" && wallet.readyState === "Installed"),
    [wallets]
  );

  /////// FILTERS & SORTERS
  // filter on preset
  const presetFilter = React.useCallback(
    (wallet: Wallet) => selectedPreset?.includes(wallet.adapter.name),
    [selectedPreset]
  );

  // filter on installed
  const installedFilter = React.useCallback((wallet: Wallet) => wallet.readyState === "Installed", []);

  // sorting on backpack
  const backpackSort = React.useCallback((a: Wallet, b: Wallet) => {
    if (a.adapter.name === "Backpack" && b.adapter.name !== "Backpack") {
      return -1;
    } else if (a.adapter.name !== "Backpack" && b.adapter.name === "Backpack") {
      return 1;
    }
    return 0;
  }, []);

  // updating wallets
  const addAbsentWallets = (allWallets: Wallet[]) => {
    const walletNames = allWallets.map((wallet) => wallet.adapter.name);
    let newWallets = allWallets;

    if (!walletNames.includes(PhantomWalletName)) {
      newWallets.push(phantomAdapter);
    }

    if (!walletNames.includes(SolflareWalletName)) {
      newWallets.push(solflareAdapter);
    }

    if (!walletNames.includes(BackpackWalletName)) {
      newWallets.push(backpackAdapter);
    }

    return newWallets;
  };

  const updateWallets = React.useCallback(
    (wallet: Wallet): ExtendedWallet => {
      const adapterName = wallet.adapter.name;
      let adapter: ExtendedWallet;
      if (adapterName === "Backpack") {
        adapter = {
          ...(isBackpackInstalled ? wallet : backpackAdapter),
        };
      } else if (adapterName === "Phantom") {
        adapter = {
          ...(isPhantomInstalled ? wallet : phantomAdapter),
        };
      } else if (adapterName === "Solflare") {
        adapter = {
          ...(isSolflareInstalled ? wallet : solflareAdapter),
        };
      } else {
        adapter = {
          ...wallet,
        };
      }

      return {
        ...adapter,
        deeplink: walletDeepLinkMap[wallet.adapter.name],
        installLink: walletInstallMap[wallet.adapter.name],
      };
    },
    [isBackpackInstalled, isPhantomInstalled, isSolflareInstalled]
  );

  const filteredWallets = React.useMemo(() => {
    let formattedWallets: ExtendedWallet[] = wallets;
    if (selectedPreset) formattedWallets = formattedWallets.filter(presetFilter);
    console.log({ one: formattedWallets });

    formattedWallets = formattedWallets.filter(installedFilter);
    console.log({ two: formattedWallets });

    formattedWallets = addAbsentWallets(formattedWallets);
    console.log({ tree: formattedWallets });

    formattedWallets = formattedWallets.sort(backpackSort);
    console.log({ four: formattedWallets });

    formattedWallets = formattedWallets.map(updateWallets);
    console.log({ five: formattedWallets });

    return formattedWallets;
  }, [
    backpackSort,
    installedFilter,
    preset,
    presetFilter,
    selectedPreset,
    wallets,
    isMobile,
    isBackpackInstalled,
    isPhantomInstalled,
  ]);

  return filteredWallets;
}

type WalletLinkMap = { [wallet: string]: string };

export const walletDeepLinkMap: WalletLinkMap = {
  Phantom: "https://phantom.app/ul/browse/https://app.marginfi.com?ref=https://app.marginfi.com",
  Backpack: "https://backpack.app/ul/browse/https://app.marginfi.com?ref=https://app.marginfi.com",
  Solflare: "https://solflare.com/ul/browse/https://app.marginfi.com?ref=https://app.marginfi.com",
};

export const walletInstallMap: WalletLinkMap = {
  Phantom: "https://phantom.app/download",
  Backpack: "https://backpack.app/download",
  Solflare: "https://gamma.solflare.com/download#extension",
};

const backpackAdapter: Wallet = {
  adapter: {
    name: BackpackWalletName,
    icon: "data:image/png;base64,",
    readyState: WalletReadyState.NotDetected,
  } as any,
  readyState: WalletReadyState.NotDetected,
};

const phantomAdapter: Wallet = {
  adapter: {
    name: PhantomWalletName,
    icon: "data:image/svg+xml;base64,",
    readyState: WalletReadyState.NotDetected,
  } as any,
  readyState: WalletReadyState.NotDetected,
};

const solflareAdapter: Wallet = {
  adapter: {
    name: SolflareWalletName,
    icon: "data:image/svg+xml;base64,",
    readyState: WalletReadyState.NotDetected,
  } as any,
  readyState: WalletReadyState.NotDetected,
};
